class makeArray {
	int[] arr;
	
	public void setArray(int[] arr) {
		this.arr = arr;
	}
	
	public void printArr() {
		for (int i = 0; i < arr.length; i++) {
			System.out.print(arr[i] + "\t"); 
		}
	}
}

